// 範例主程式，示範 SwordsMan 與 Magician 的基本互動（繁體中文註解與輸出）
public class Main {
    public static void main(String[] args) {
        // 建立兩個劍士並互相攻擊
        SwordsMan swords1 = new SwordsMan("亞瑟", 50, 12);
        SwordsMan swords2 = new SwordsMan("蘭斯", 45, 10);

        swords1.attack(swords2);
        swords2.attack(swords1);
        System.out.println(swords1.getName() + " 血量：" + swords1.getHealth());
        System.out.println(swords2.getName() + " 血量：" + swords2.getHealth());

        // 建立兩個魔法師，示範攻擊與治療
        Magician mag1 = new Magician("梅林", 35, 8);
        Magician mag2 = new Magician("莫甘娜", 30, 9);

        mag1.attack(mag2);
        mag1.heal(mag1); // 自我治療示範
        System.out.println(mag1.getName() + " 血量：" + mag1.getHealth());
        System.out.println(mag2.getName() + " 血量：" + mag2.getHealth());
    }
}

public class Role {

    private String name;
    private int health;
    private int attackPower;

    public Role(String name, int health, int attackPower) {
        this.name = name;
        this.health = health;
        this.attackPower = attackPower;
    }

    public String getName() {
        return name;
    }

    public int getHealth() {
        return health;
    }

    // 取得攻擊力（新增：攻擊力為 private，需要公開 getter 供子類別使用）
    public int getAttackPower() {
        return attackPower;
    }

    public void setHealth(int health) {
        this.health = health;
    }

    public boolean isAlive() {
        return health > 0;
    }
}

public class Role {

    private String name;
    private int health;
    private int attackPower;

    public Role(String name, int health, int attackPower) {
        this.name = name;
        this.health = health;
        this.attackPower = attackPower;
    }

    public String getName() {
        return name;
    }

    public int getHealth() {
        return health;
    }

    // 取得攻擊力（新增：攻擊力為 private，需要公開 getter 供子類別使用）
    public int getAttackPower() {
        return attackPower;
    }

    public void setHealth(int health) {
        this.health = health;
    }

    public boolean isAlive() {
        return health > 0;
    }
}

public class SwordsMan extends Role{

    // 建構子 : 初始化角色的名稱、血量和攻擊力
    public SwordsMan(String name, int health, int attackPower) {
        super(name, health, attackPower);
    }

    // 角色攻擊另一個角色
    public void attack(SwordsMan opponent) {
        opponent.setHealth(opponent.getHealth() - this.getAttackPower());
        // 顯示攻擊資訊（繁體中文）
        System.out.println(this.getName() + " 攻擊 " + opponent.getName() + "，造成 " + this.getAttackPower() + " 點傷害。" );
    }

}

public class SwordsMan extends Role{

    // 建構子 : 初始化角色的名稱、血量和攻擊力
    public SwordsMan(String name, int health, int attackPower) {
        super(name, health, attackPower);
    }

    // 角色攻擊另一個角色
    public void attack(SwordsMan opponent) {
        opponent.setHealth(opponent.getHealth() - this.getAttackPower());
        // 顯示攻擊資訊（繁體中文）
        System.out.println(this.getName() + " 攻擊 " + opponent.getName() + "，造成 " + this.getAttackPower() + " 點傷害。" );
    }

}
